import React, { Component } from 'react';
import { useParams } from "react-router-dom";
// importando modulos de las paginas
import Sidebar from './Sidebar';
import axios from "axios";
import Moment from 'react-moment';
import 'moment/locale/es'; 
 
const baseURL = "http://localhost:3900/api/article/";
 
 
export default function Details() {
 
    const { id } = useParams();
    const [article, setArticle] = React.useState(null);
    React.useEffect(() => {
        axios.get(baseURL + id).then((response) => {
            setArticle(response.data.article);
 
        });
    }, []);
 
    if (!article) return null;
    return (
 
        <div className="center">
            <section id="content">
                {article &&
                    <article className="article-item article-detail" >
 
                        <div className="image-wrap">
                        {article.image !== null ? (
                                  <img src={this.url+'get-image/'+ article.image}  alt={article.title}/>
                                ) : (
                                    <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/d/d1/Image_not_available.png/640px-Image_not_available.png"  alt="description of image"/>
                                )
                            }
                        </div>
 
                        <h1 className="subheader">{article.title}</h1>
                            <span className="date">
                                <Moment locale ='es' fromNow>{article.Date}</Moment>
                            </span>
                            <p>
                               {article.content}
                            </p>    
                          

                            <div className="clearfix"></div>
                        </article>

                    }




                </section>
                <Sidebar />
            </div>
        );
    }
