import React, { Component } from 'react';
import {BrowserRouter, Route, Routes,useParams} from 'react-router-dom'; // In react-router-dom Switch => Routes 



import SeccionPruebas from './components/SeccionPruebas';
import MiComponente from './components/MiComponente';
import Peliculas from './components/Peliculas';
import Error from './components/Error';

class Router extends Component {

    render() {
        function PruebaParametros (){
             let params = useParamas();
             let {apellidos} = useParamas();

             let sinApellidos = (null);

             if(apellidos){
                sinApellidos = (
                    <h2 className="subheader">Prueba obtenes apellidos: {apellidos}</h2>
                );
             }

             return (
                <div>
                    <h2 className='subheader'> Prueba obtener nombre : {params.nombre}</h2>
                    {sinApellidos}
                    
                </div>
             );
        }


        return (
            <BrowserRouter>
                 {/** Configurar Rutas y paginas */}
                <Routes>
                    <Route exact path="/" element={<Peliculas />} />
                    <Route exact path = "/ruta-prueba" element = {<SeccionPruebas />} /> {/** Components => elements  And Decaration Components is whit <component/> */}
                    <Route exact path = "/segunda-ruta" element = {<MiComponente />} />
                    <Route path ="*" element = {<Error />} />
                    {/**Mas opciones de rutas 
                    <Route exact path="/pagina-1" render = {()=> (
                        <div>
                            <h1>Hola mundo desde la ruta pagina1</h1>
                        </div>
                        
                    )} />*/}
                    <Route exact path ="/pruebas/:nombre" element = {<PruebaParametros/>}/>
                    <Route exact path ="/pruebas/:nombre/:apellidos" element = {<PruebaParametros/>}/>
                </Routes>
            </BrowserRouter>

        )
    }

}

export default Router;
