import React, { Component } from 'react';

//import Slider from "./Slider";
import Sidebar from "./Sidebar";

class Blog extends Component {

    nombreRef = React.createRef();
    apellidosRef = React.createRef();
    bioRef = React.createRef();
    generoHombreRef = React.createRef();
    generoMujerRef = React.createRef();
    generoOtrosRef = React.createRef();

    recibirFormulario = (e) => {
        e.preventDefault(); // Previene las acciones default de los formulario o x funcion
        alert("Formulario enviado!");
    }

    render() {

        return (
            <div id="formulario">
                <div className='center'>
                    <div id='content'>
                    <h2 className="subheader">Formulario</h2>
                        {/**Crear un formulario*/}

                        <form class="mid-form" onSubmit={this.recibirFormulario}>
                            <div class="form-group">
                                <label for="nombre">Nombre</label>
                                <input type="text" name="nombre" ref= { this.nombreRef}/>
                            </div>

                            <div class="form-group">
                                <label for="apellidos">Apellidos</label>
                                <input type="text" name="apellidos" ref= { this.apellidosRef}/>
                            </div>

                            <div class="form-group">
                                <label for="bio">Biografia</label>
                                <textarea name="bio"ref= { this.bioRedf}></textarea>
                            </div>

                            <div class="form-group radibuttons">
                                <input type="radio" name="genero" value="hombre" /> Hombre
                                <input type="radio" name="genero" value="mujer" /> Mujer
                                <input type="radio" name="genero" value="otro" /> Otro
                            </div>

                            <div class="clearfix"></div>

                            <input type="submit" value="Enviar" class="btn btn-success" />

                        </form>
                    </div>
                </div>
                <Sidebar
                    blog="false"
                />
            </div>

        )
    }
}

export default Blog;
