import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import Global from "../Global";
import Sidebar from "./Sidebar";

//Validacion de formularios y alertas

class CreateArticle extends Component {


    titleRef = React.createRef();
    contentRef = React.createRef();

    saveArticle(e) {
        
        
        
        e.preventDefault();
        alert(this.titleRef.current.value);
        alert(this.contentRef.current.value);
    }
    render() {
        return (
            <div className="center">
                <section id="content">
                    <h1 className="subheader">Crear Articulo</h1>
                    <form action="" className="mid-form" onSubmit={this.saveArticle}>

                        <div className="form-group">
                            <label htmlFor="title">Titulo</label>
                            <input type="text" name="title" id="" ref={this.titleRef}/>
                        </div>

                        <div className="form-group">
                            <label htmlFor="content">Titulo</label>
                            <textarea  name="content" id="" ref={this.contentRef}/>
                        </div>
                        
                        <div className="form-group"> 
                            <label htmlFor="file0">Imagen</label>
                            <input type='file'  name="file0" id="" />
                        </div>

                        <input type="submit" value="Guardar" className="btn btn-success" />
                    </form>
                </section>

                <Sidebar/>
            </div>
        );
    }
}

export default CreateArticle;
